		int i = 5;
		double db1;
		float fl1;
		num1 = 22%7;
		db1 = 3433;
		fl1 = 22/7;
		char ch ='s';
		int bl = 25;
		boolean output = false;
		
		System .out.println(ch);
		System .out.println(num1);
		System .out.println(db1);
		System .out.println(fl1);
